class Solution {
public:
    array<int,3> maxx(vector<vector<int>>& v)
    {
        int n=v.size();
        vector<array<int,2>> s(n), d(n);
        for(int i=0;i<n;i++)
        {
            s[i]={v[i][0]+v[i][1],i};
            d[i]={v[i][0]-v[i][1],i};
        }
        sort(s.begin(),s.end());
        sort(d.begin(),d.end());
        if(s[n-1][0]-s[0][0]>d[n-1][0]-d[0][0])
            return {s[n-1][0]-s[0][0],s[n-1][1],s[0][1]};
        return {d[n-1][0]-d[0][0],d[n-1][1],d[0][1]};
    }
    int minimumDistance(vector<vector<int>>& points) {
        auto ini=maxx(points);
        auto p1=points;
        auto p2=points;
        p1.erase(p1.begin()+ini[1]);
        p2.erase(p2.begin()+ini[2]);
        auto a1=maxx(p1);
        auto a2=maxx(p2);
        return min(a1[0],a2[0]);
    }
};
