class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<pair<int,int>>dp1(n), dp2(n), dp3(n), dp4(n);
        for(int i = 0; i < n; i++) {
            dp1[i] = {points[i][0] + points[i][1],i};
            dp2[i] = {points[i][0] - points[i][1],i};
            dp3[i] = {-points[i][0] + points[i][1],i};
            dp4[i] = {-points[i][0] - points[i][1],i};
        }
        sort(dp1.begin(),dp1.end());
        sort(dp2.begin(),dp2.end());
        sort(dp3.begin(),dp3.end());
        sort(dp4.begin(),dp4.end());
        int ans = INT_MAX;
        // 枚举不用的点
        for(int i = 0; i < n; i++) {
            int cur1,cur2,cur3,cur4;
            if(i != dp1.back().second)
                cur1 = dp1.back().first;
            else
                cur1 = dp1[dp1.size()-2].first;
            if(i != dp1.front().second)
                cur1 -= dp1.front().first;
            else
                cur1 -= dp1[1].first;
            if(i != dp2.back().second)
                cur2 = dp2.back().first;
            else
                cur2 = dp2[dp2.size()-2].first;
            if(i != dp2.front().second)
                cur2 -= dp2.front().first;
            else
                cur2 -= dp2[1].first;
            if(i != dp3.back().second)
                cur3 = dp3.back().first;
            else
                cur3 = dp3[dp3.size()-2].first;
            if(i != dp3.front().second)
                cur3 -= dp3.front().first;
            else
                cur3 -= dp3[1].first;
            if(i != dp4.back().second)
                cur4 = dp4.back().first;
            else
                cur4 = dp4[dp4.size()-2].first;
            if(i != dp4.front().second)
                cur4 -= dp4.front().first;
            else
                cur4 -= dp4[1].first;
            ans = min(ans,max({cur1,cur2,cur3,cur4}));
        }
        return ans;
    }
};
