class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        int n=a.size();
        vector<int>b(n),c(n);
        for(int i=0;i<n;i++)
        {
            b[i]=a[i][0]+a[i][1];
            c[i]=a[i][0]-a[i][1];
        }
        vector<int>d=b,e=c;
        sort(d.begin(),d.end());
        sort(e.begin(),e.end());
        int ans=1e9;
        for(int i=0;i<n;i++)
        {
            int bl=d[0];
            if(b[i]==d[0])bl=d[1];
            int br=d[n-1];
            if(b[i]==d[n-1])br=d[n-2];
            int cl=e[0];
            if(c[i]==e[0])cl=e[1];
            int cr=e[n-1];
            if(c[i]==e[n-1])cr=e[n-2];
            ans=min(ans,max(br-bl,cr-cl));
        }
        return ans;
    }
};
