class Solution {
public:
    typedef pair<int, int> PII;
#define x first
#define y second
    int minimumDistance(vector<vector<int>>& p) {
        set<PII> s[4];
        int a[2] = {1, -1};
        for (int k = 0;k < p.size();++ k) {
            auto&t = p[k];
            for (int i = 0;i < 2;++ i) for(int j = 0;j < 2;++ j) {
                s[i * 2 + j].insert({t[0] * a[i] + t[1] * a[j], k});
            }
        }
        int ans = 1e9;
        for (int k = 0;k < p.size();++ k) {
            auto&t = p[k];
            for (int i = 0;i < 2;++ i) for(int j = 0;j < 2;++ j) {
                s[i * 2 + j].erase({t[0] * a[i] + t[1] * a[j], k});
            }
            int res = 0;
            for (int i = 0;i < 4;++ i) {
                res = max(res, s[i].rbegin()->x - s[i].begin()->x);
            }
            ans = min(ans, res);
            for (int i = 0;i < 2;++ i) for(int j = 0;j < 2;++ j) {
                s[i * 2 + j].insert({t[0] * a[i] + t[1] * a[j], k});
            }
        }
        return ans;
    }
};
