func minimumDistance(points [][]int) int {
	maxXAY := math.MinInt
	minXAY := math.MaxInt
	maxXSY := math.MinInt
	minXSY := math.MaxInt
	a, b, c, d := 0, 0, 0, 0
	for i, v := range points {
		if v[0]+v[1] > maxXAY {
			maxXAY = v[0] + v[1]
			a = i
		}
		if v[0]+v[1] < minXAY {
			minXAY = v[0] + v[1]
			b = i
		}
		if v[0]-v[1] > maxXSY {
			maxXSY = v[0] - v[1]
			c = i
		}
		if v[0]-v[1] < minXSY {
			minXSY = v[0] - v[1]
			d = i
		}
	}
	maxL := 0
	x, y := 0, 0
	for i, v := range points {
		al := abs(v[0] + v[1] - maxXAY)
		bl := abs(v[0] + v[1] - minXAY)
		cl := abs(v[0] - v[1] - maxXSY)
		dl := abs(v[0] - v[1] - minXSY)
		t := max(max(al, bl), max(cl, dl))
		if t > maxL {
			if t == al {
				x, y = a, i
			} else if t == bl {
				x, y = b, i
			} else if t == cl {
				x, y = c, i
			} else {
				x, y = d, i
			}
			maxL = t
		}
	}

	check := func(t int) int {
		maxXAY := math.MinInt
		minXAY := math.MaxInt
		maxXSY := math.MinInt
		minXSY := math.MaxInt
		for i, v := range points {
			if i == t {
				continue
			}
			if v[0]+v[1] > maxXAY {
				maxXAY = v[0] + v[1]
				a = i
			}
			if v[0]+v[1] < minXAY {
				minXAY = v[0] + v[1]
				b = i
			}
			if v[0]-v[1] > maxXSY {
				maxXSY = v[0] - v[1]
				c = i
			}
			if v[0]-v[1] < minXSY {
				minXSY = v[0] - v[1]
				d = i
			}
		}
		maxL := 0
		for i, v := range points {
			if i == t {
				continue
			}
			al := abs(v[0] + v[1] - maxXAY)
			bl := abs(v[0] + v[1] - minXAY)
			cl := abs(v[0] - v[1] - maxXSY)
			dl := abs(v[0] - v[1] - minXSY)
			t := max(max(al, bl), max(cl, dl))
			maxL = max(t, maxL)
		}
		return maxL
	}
	return min(check(x), check(y))
