class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        multiset<int> s1,s2;
        int n=p.size();
        for(int i=0;i<n;i++){
            s1.insert(p[i][0]+p[i][1]);
            s2.insert(p[i][0]-p[i][1]);
        }
        int ans=1e9;
        for(int i=0;i<n;i++){
            auto it=s1.find(p[i][0]+p[i][1]);s1.erase(it);
            auto pt=s2.find(p[i][0]-p[i][1]);s2.erase(pt);
            it=s1.end();it--;pt=s2.end();pt--;

            ans=min(ans,max((*it)-(*s1.begin()),(*pt)-(*s2.begin())));

            s1.insert(p[i][0]+p[i][1]);            
            s2.insert(p[i][0]-p[i][1]);


        }
        return ans;
    }
};
