class Solution {
public:
    pair<int,int> find(vector<vector<int>>& points,vector<int>&visited)
    {
        int n = points.size();
        int maxy=-1;
        int mixy=-1;
        int yx=-1;
        int xy=-1;
        int ans =-1;
        int p1=-1;
        int p2=-1;

        for(int i=0;i<n;i++)
        {

            if(visited[i]==0)
            {
                if(maxy !=-1)
                {
                    int d = abs(points[maxy][0]-points[i][0])+abs(points[maxy][1]-points[i][1]);
                    if(d>ans)
                    {
                        p1 = i;
                        p2 = maxy;
                        ans = d;
                    }
                }
                if(mixy !=-1)
                {
                     int d = abs(points[mixy][0]-points[i][0])+abs(points[mixy][1]-points[i][1]);
                    if(d>ans)
                    {
                        p1 = i;
                        p2 = mixy;
                        ans = d;
                    }
                }
                if(yx !=-1)
                {
                     int d = abs(points[yx][0]-points[i][0])+abs(points[yx][1]-points[i][1]);
                    if(d>ans)
                    {
                        p1 = i;
                        p2 = yx;
                        ans = d;
                    }
                }
                if(xy !=-1)
                {
                     int d = abs(points[xy][0]-points[i][0])+abs(points[xy][1]-points[i][1]);
                    if(d>ans)
                    {
                        p1 = i;
                        p2 = xy;
                        ans = d;
                    }
                }

                if(maxy <0 || points[maxy][0]+points[maxy][1]<points[i][0]+points[i][1])
                    maxy =i;
                if(mixy <0 || points[mixy][0]+points[mixy][1]>points[i][0]+points[i][1] )
                    mixy = i;
                if(yx <0 ||  points[yx][1]-points[yx][0]<points[i][1]-points[i][0])
                    yx = i;
                if(xy <0 || points[xy][0]-points[xy][1]<points[i][0]-points[i][1])
                    xy =i;

            }

        }
        return {p1,p2};

    }
    int minimumDistance(vector<vector<int>>& points) {
        sort(points.begin(),points.end());
        int n = points.size();
        vector<int>visited(n,0);
        pair<int,int>p = find(points,visited);
        visited[p.first]=1;
        pair<int,int>p1 =  find(points,visited);
        int ans = abs(points[p1.first][0]-points[p1.second][0]) + abs(points[p1.second][1]-points[p1.first][1]);
        visited[p.first]=0;
        visited[p.second]=1;
        p1 =  find(points,visited);
        ans = min(ans,abs(points[p1.first][0]-points[p1.second][0]) + abs(points[p1.second][1]-points[p1.first][1]));
        return ans;


    }
};
