class Solution {
public:
    int v0(int i, vector<int>& ids, vector<vector<int>>&  A) {
        int x = ids[i];
        return  A[x][0] + A[x][1];
    }
    int v1(int i, vector<int>& ids, vector<vector<int>>&  A) {
        int x = ids[i];
        return  A[x][0] - A[x][1];
    }
    int dist(vector<vector<int>>& A, int j)
    {

        int n = A.size();
         // cout << j << "," << n << endl;
        // Vectors to store maximum and
        // minimum of all the four forms
        vector<int> id(n - 1), id1(n - 1);
        int x = 0;
        for (int i = 0; i < n; i++) {
            if (i == j) continue;
            id[x] = i;
            id1[x] = i;
            x++;
        }
        //cout << "size " <<  id.size() << endl;
        sort(id.begin(), id.end(), [&A] (int x, int y) {
            return A[x][0] + A[x][1] <  A[y][0] + A[y][1];
        });

        sort(id1.begin(), id1.end(), [&A] (int x, int y) {
            return A[x][0] - A[x][1] <  A[y][0] - A[y][1];
        });
        int a = v0(n - 2, id, A) - v0(0, id, A);
        int b = v1(n - 2, id1, A) - v1(0, id1, A);
        int c =  max(a, b);
        //cout << "ans " << c << "," << j << endl;
        return c;
    }
    int MaxDist(vector<vector<int>>&  A)
    {
        int n = A.size();
        // Vectors to store maximum and
        // minimum of all the four forms
        vector<int> id(n), id1(n);
        for (int i = 0; i < n; i++) {
            id[i] = i;
            id1[i] = i;
        }
        sort(id.begin(), id.end(), [&A] (int x, int y) {
            return A[x][0] + A[x][1] <  A[y][0] + A[y][1];
        });

        sort(id1.begin(), id1.end(), [&A] (int x, int y) {
            return A[x][0] - A[x][1] <  A[y][0] - A[y][1];
        });

        /**
        for (int i = 0; i < n; i++) {
            V[i] = A[i][0] + A[i][1];
            V1[i] = A[i][0] - A[i][1];
        }

        // Sorting both the vectors
        sort(V.begin(), V.end());
        sort(V1.begin(), V1.end());
        **/
        int a = dist(A, id[0]);
        int b = dist(A, id[n - 1]);
        int a1 = dist(A, id1[0]);
        int b1 = dist(A, id1[n - 1]);

        return min(min(a, b), min(a1, b1));
    }
    int minimumDistance(vector<vector<int>>& ps) {
        return MaxDist(ps);

    }
};
