class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int ans = 1e9;
        multiset<int>m1,m2;

        for(auto p: points){
            int x = p[0];
            int y = p[1];
            m1.insert(x+y);
            m2.insert(x-y);
        }

        for(auto p:points){
            int x = p[0];
            int y = p[1];
            m1.erase(m1.find(x+y));
            m2.erase(m2.find(x-y));

            int f1 = *m1.rbegin() - *m1.begin();
            int f2 = *m2.rbegin() - *m2.begin();

            int cur = max(f1,f2);
            ans = min(ans,cur);

            m1.insert(x+y);
            m2.insert(x-y);
        }
        return ans;
    }
};
