class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int n = A.size();
        vector<vector<long long>> pref,suff;
        ll minsum=INF,maxsum=-INF,mindiff=INF,maxdiff=-INF;
        pref.pb({minsum,maxsum,mindiff,maxdiff});
        minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];
        for(int i=1;i<n;i++){
            pref.pb({minsum,maxsum,mindiff,maxdiff});
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum)
                minsum = sum;
            else if (sum > maxsum)
                maxsum = sum;
            if (diff < mindiff)
                mindiff = diff;
            else if (diff > maxdiff)
                maxdiff = diff;
        }
        minsum=INF,maxsum=-INF,mindiff=INF,maxdiff=-INF;
        suff.pb({minsum,maxsum,mindiff,maxdiff});
        minsum = maxsum = A[n-1][0] + A[n-1][1];
        mindiff = maxdiff = A[n-1][0] - A[n-1][1];
        for(int i=n-2;~i;i--){
            suff.pb({minsum,maxsum,mindiff,maxdiff});
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum)
                minsum = sum;
            else if (sum > maxsum)
                maxsum = sum;
            if (diff < mindiff)
                mindiff = diff;
            else if (diff > maxdiff)
                maxdiff = diff;
        }


        ll ans = INF;

        fr(i,n){
            if(pref[i][0]==INF){
                ans = min(ans, max(suff[n-i-1][1]-suff[n-i-1][0],suff[n-i-1][3]-suff[n-i-1][2]));
                continue;
            }
            if(suff[n-i-1][0]==INF){
                ans = min(ans, max(pref[i][1]-pref[i][0],pref[i][3]-pref[i][2]));
                continue;
            }
            ll a=max(pref[i][1],suff[n-i-1][1]), b = min(pref[i][0],suff[n-i-1][0]);
            ll c = max(pref[i][3],suff[n-i-1][3]), d = min(pref[i][2],suff[n-i-1][2]);
            ans = min(ans,max(a-b,c-d));
        }
        return (int)ans;
    }
};
