using  PII = pair<int, int >;
class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        int n = a.size();
        vector<PII> f(n), g(n);
        for(int i = 0; i < n; i ++ ){
            f[i] = {a[i][0] - a[i][1], i};
            g[i] = {a[i][0] + a[i][1], i};
        }
        ranges::sort(f);
        ranges::sort(g);
        PII ps;;
        if(f[n - 1].first - f[0].first > g[n - 1].first - g[0].first){
            ps = {f[n - 1].second, f[0].second};
        } else {
            ps = {g[n - 1].second, g[0].second};
        }


        int idx = 0;
        for(int i = 0; i < n; i ++ ){
            if(i == ps.first) continue;
            f[idx] = {a[i][0] - a[i][1], i};
            g[idx ++] = {a[i][0] + a[i][1], i};
        }
        sort(f.begin(), f.begin() + n - 1);
        sort(g.begin(), g.begin() + n - 1);
        int ans = max(f[n - 2].first - f[0].first, g[n - 2].first - g[0].first);
        idx = 0;
        for(int i = 0; i < n; i ++ ){
            if(i == ps.second) continue;
            f[idx] = {a[i][0] - a[i][1], i};
            g[idx ++] = {a[i][0] + a[i][1], i};
        }
        sort(f.begin(), f.begin() + n - 1);
        sort(g.begin(), g.begin() + n - 1);
        ans = min(ans, max(f[n - 2].first - f[0].first, g[n - 2].first - g[0].first));
        return ans;
    }
};
