class Solution {
public:
    int minimumDistance(vector<vector<int>>& v) {
        int ans = INT_MAX;
        multiset<int> s1, s2;
        for (auto it : v) {
            int x = it[0], y = it[1];
            s1.emplace(x+y);
            s2.emplace(x-y);
        }

        for (auto it : v) {
            int x = it[0], y = it[1];
            s1.erase(s1.find(x+y));
            s2.erase(s2.find(x-y));
            ans = min(ans, max(*s1.rbegin()-*s1.begin(), *s2.rbegin() - *s2.begin()));
            s1.emplace(x+y);
            s2.emplace(x-y);
        }
        return ans;
    }
};
