class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        set<pair<int,int>>sum,diff;
        int n=p.size();
        for(int i=0;i<n;i++)
        {
            sum.insert({(p[i][0]+p[i][1]),i});
            diff.insert({(p[i][0]-p[i][1]),i});
        }
        int ans=INT_MAX;
        for(int i=0;i<n;i++)
        {
            sum.erase({p[i][0]+p[i][1],i});
            diff.erase({p[i][0]-p[i][1],i});
            int tem1=(*(--sum.end())).first-(*sum.begin()).first;
            int tem2=(*(--diff.end())).first-(*diff.begin()).first;
            ans=min(ans,max(tem1,tem2));
           sum.insert({(p[i][0]+p[i][1]),i});
            diff.insert({(p[i][0]-p[i][1]),i});
        }
        return ans;
    }
};
