class Solution {
public:

    int getmax(vector<vector<int>>& pts, int x)
    {
        vector<int> ns,ds;
        for(int i =0 ; i<pts.size(); i++)
        {
            if(i==x)
                continue;
            ns.push_back(pts[i][0]+pts[i][1]);
            ds.push_back(pts[i][0]-pts[i][1]);
        }
        sort(ns.begin(),ns.end());
        sort(ds.begin(),ds.end());
        return max(ns.back()-ns[0],ds.back()-ds[0]);
    }

    int minimumDistance(vector<vector<int>>& pts) {
        int n = pts.size();
        vector<pair<int,int>> s(n),d(n);
        int i;
        for(i = 0; i<n; i++)
        {
            s[i] = {pts[i][0]+pts[i][1],i};
            d[i] = {pts[i][0]-pts[i][1],i};
        }
        sort(s.begin(),s.end());
        sort(d.begin(),d.end());

        int x,y;
        if(s.back().first-s[0].first>d.back().first-d[0].first)
        {
            x = s.back().second;
            y = s[0].second;
        }
        else
        {
            x = d.back().second;
            y = d[0].second;
        }
        // cout << x << ' ' << y << endl;
        int ans = INT_MAX;
        ans = min(ans, getmax(pts,x));
        ans = min(ans, getmax(pts,y));

        return ans;
    }
};
