class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int, int>> s1;
        vector<pair<int, int>> s2;
        auto psize = points.size();
        int id = 0;
        for(auto &p: points){
            s1.emplace_back(p[0]+p[1], id);
            s2.emplace_back(p[0]-p[1], id);
            id++;
        }
        sort(s1.begin(), s1.end());
        sort(s2.begin(), s2.end());
        if(s1.back().first-s1[0].first< s2.back().first-s2[0].first){
            swap(s1, s2);
        }
        vector<int> res;
        int res1, res2;
        //去掉左侧
        {
            int s1l = 0; 
            int s1r = points.size()-1;
            int s2l = 0; 
            int s2r = points.size()-1;
            if(s1[s1l].second==s2[s2r].second){
                s2r--;
            }else if(s1[s1l].second==s2[s2l].second){
                s2l++;
            }
            s1l++;
            res1=max(s1[s1r].first-s1[s1l].first, s2[s2r].first-s2[s2l].first);
        }
        //去掉右侧
        {
            int s1l = 0; 
            int s1r = points.size()-1;
            int s2l = 0; 
            int s2r = points.size()-1;
            if(s1[s1r].second==s2[s2r].second){
                s2r--;
            }else if(s1[s1r].second==s2[s2l].second){
                s2l++;
            }
            s1r--;
            res2=max(s1[s1r].first-s1[s1l].first, s2[s2r].first-s2[s2l].first);
        }
        return min(res1, res2);
    }
};
