class Solution {
public:
    int minimumDistance(vector<vector<int>>& pt) {
        int n=pt.size();
        vector<vector<int>> a(n),b(n);
        for(int i=0;i<n;i++){
            a[i]={pt[i][0]+pt[i][1],i};
            b[i]={pt[i][0]-pt[i][1],i};
        }
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        int ret=1e9;
        auto rem = [&](int i){
            vector<int> arr,brr;
            for(int j=0;j<n;j++){
                if(i!=j){
                    arr.push_back(pt[j][0]+pt[j][1]);
                    brr.push_back(pt[j][0]-pt[j][1]);
                }
            }
            sort(arr.begin(),arr.end());
            sort(brr.begin(),brr.end());
            return max(arr.back()-arr.front(),brr.back()-brr.front());
        };
        ret=min(ret,rem(a[0][1]));
        ret=min(ret,rem(a[n-1][1]));
        ret=min(ret,rem(b[0][1]));
        ret=min(ret,rem(b[n-1][1]));
        return ret;
    }
};
