class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        int n = a.size();
        multiset<int> se, sr;
        for(int i=0; i<n; i++){
            se.insert(a[i][0] + a[i][1]);
            sr.insert(a[i][0] - a[i][1]);
        }
        int ans = 1e9;
        for(int i=0; i<n; i++){
            int r = a[i][0] + a[i][1];
            int d = a[i][0] - a[i][1];
            se.erase(se.find(r));
            sr.erase(sr.find(d));
            int p = *--se.end() - *se.begin();
            int q = *--sr.end() - *sr.begin();
            int mx = max(p, q);
            ans = min(ans, mx);
            se.insert(r);
            sr.insert(d);
        }
        return ans;
    }
};
