// clang-format off

ll ob(ll i, ll n) { return i < 0 || i >= n; }
ll tp(ll x) { return ( 1LL << x ); }
ll rup(ll a, ll b) { return a % b ? a/b + 1 : a/b; }
ll sign(ll x) {	return x == 0 ? 0 : x / abs(x); }
void makemod(ll& x, ll m) { x %= m; if (x < 0) { x += m; } }
ll getmod(ll x, ll m) { makemod(x, m); return x; }
ll powmod(ll a, ll b, ll m) { if (b == 0) return 1; ll h = powmod(a, b/2, m); ll ans = h*h%m; return b%2 ? ans*a%m : ans; }
ll invmod(ll a, ll m) { return powmod(a, m - 2, m); }
void inll(ll& x) { scanf("%lld", &x); }

template <typename A, typename B> bool upmin(A& x, B v) { if (v >= x) return false; return x = v, true; }
template <typename A, typename B> bool upmax(A& x, B v) { if (v <= x) return false; return x = v, true; }
// clang-format on

const VLL di = {0, 0, 1, -1, 1, -1, 1, -1}, dj = {1, -1, 0, 0, -1, -1, 1, 1};
const ll inf = 1e18;
const ll mod = 1e9 + 7;
const string vowels = "aeiou";
ll isvowel(char c) {
    c = tolower(c);
    return vowels.find(c) != string::npos;
}

class Solution {
public:
    pair<VLL, ll> once(vector<vector<int>>& A) {
        ll mid = A[0][1];
        ll hi = mid;
        ll hiInd = 0;
        ll lo = mid;
        ll loInd = 0;

        ll farthest = -inf;
        VLL ans;

        rep(i, 1, A.size()) {
            auto& a = A[i];

            ll x = a[0];
            ll y = a[1];

            ll dist = -inf;
            ll ind;
            // if (y >= mid) {
                if (lo != inf) {
                    if (upmax(dist, abs(x - A[0][0]) + abs(y - lo))) {
                        ind = loInd;
                    }
                }
            // }

            // if (y <= mid) {
                if (hi != -inf) {
                    if (upmax(dist, abs(x - A[0][0]) + abs(y - hi))) {
                        ind = hiInd;
                    }
                }
            // }

            if (upmax(farthest, dist)) {
                ans = {i, ind};
            }

            if (y >= mid) {
                // see how u cross with
                if (upmax(hi, y - (x - A[0][0]))) {
                    hiInd = i;
                }
            }

            if (y <= mid) {
                // see how u cross with
                if (upmin(lo, y + (x - A[0][0]))) {
                    loInd = i;
                }
            }
        }

        return {ans, farthest};
    }

    int minimumDistance(vector<vector<int>>& A) {  // !
        sort(all(A));
        ll n = A.size();

        auto res = once(A);

        debug(res.first);

        ll ans = inf;
        for (auto ind : res.first) {
            vector<vector<int>> B;
            rep(i, 0, A.size()) {
                if (i == ind) continue;
                B.push_back(A[i]);
            }

            upmin(ans, once(B).second);
        }

        return ans;
    }
};
