class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        priority_queue<pair<int,int> > qxmax;
        priority_queue<pair<int,int> > qymax;
        priority_queue<pair<int,int> > qxmin;
        priority_queue<pair<int,int> > qymin;
        priority_queue<pair<int,int> > qsummax;
        priority_queue<pair<int,int> > qsummin;
        priority_queue<pair<int,int> > qsummax1;
        priority_queue<pair<int,int> > qsummin1;        
        for(int i=0;i<points.size();i++){
            qxmax.push({points[i][0],i});
            qymax.push({points[i][1],i});
            qxmin.push({-points[i][0],i});
            qymin.push({-points[i][1],i});
            qsummax.push({points[i][0]+points[i][1],i});
            qsummin.push({-(points[i][0]+points[i][1]),i});
            qsummax1.push({points[i][0]-points[i][1],i});
            qsummin1.push({-(points[i][0]-points[i][1]),i});
            if(qxmax.size()>10)
                qxmax.pop();
            if(qxmin.size()>10)
                qxmin.pop();
            if(qymax.size()>10)
                qymax.pop();
            if(qymin.size()>10)
                qymin.pop();
            if(qsummax.size()>10)
                qsummax.pop();
            if(qsummin.size()>10)
                qsummin.pop();
            if(qsummax1.size()>10)
                qsummax1.pop();
            if(qsummin1.size()>10)
                qsummin1.pop();

        }
        set<int> s1;
        while(qxmax.size()>0){
            s1.insert(qxmax.top().second);
            qxmax.pop();
        }
        while(qxmin.size()>0){
            s1.insert(qxmin.top().second);
            qxmin.pop();
        }
        while(qymax.size()>0){
            s1.insert(qymax.top().second);
            qymax.pop();
        }
        while(qymin.size()>0){
            s1.insert(qymin.top().second);
            qymin.pop();
        }
        while(qsummax.size()>0){
            s1.insert(qsummax.top().second);
            qsummax.pop();
        }
        while(qsummin.size()>0){
            s1.insert(qsummin.top().second);
            qsummin.pop();
        }
        while(qsummax1.size()>0){
            s1.insert(qsummax1.top().second);
            qsummax1.pop();
        }
        while(qsummin1.size()>0){
            s1.insert(qsummin1.top().second);
            qsummin1.pop();
        }

        vector<vector<int> > v1;
        for(auto itr=s1.begin();itr!=s1.end();itr++){
            v1.push_back(points[*itr]);
        }   
        int minanswer=1000000000;
        for(int i=0;i<v1.size();i++){
            int maxans=0;
            for(int j=0;j<v1.size();j++){
                for(int k=j+1;k<v1.size();k++){
                    if(j==i||k==i){
                        continue;
                    }
                    maxans=max(maxans,abs(v1[j][0]-v1[k][0])+abs(v1[j][1]-v1[k][1]));
                }
            }
            minanswer=min(minanswer,maxans);
        }
        return minanswer;


    }
};
