#include <bits/stdc++.h>

using namespace std;

class Solution {
public:


int d=2;

vector<int> solve(const vector<vector<int>>& a)
{

    int ans = 0;
    int n=a.size();

    int mai=0,mni=0;

    for(int i = 0 ; i < 1 << d ; i ++ )
    {
        int tmai=0,tmni=0;
        int maxv = -1e9, minv = 1e9;
        for(int j = 0 ; j < n ; j ++ )
        {
            int sum = 0;
            for(int k = 0 ; k < d ; k ++ )
            {
                if(i >> k & 1) sum += a[j][k];
                else sum -= a[j][k];
            }
            if(sum>maxv){
                maxv = sum;
                tmai=j;
            }
            if(sum<minv){
                minv = sum;
                tmni=j;
            }
        }
        if(maxv - minv> ans){
            ans=maxv - minv;
            mai=tmai;
            mni=tmni;
        }
    }
    return {mai,mni,ans};
}

    int minimumDistance(vector<vector<int>>& ps) {
        vector<int>ans = solve(ps);
        auto l=ps;
        l.erase(l.begin()+ans[0]);
        int x=solve(l)[2];

        l=ps;
        l.erase(l.begin()+ans[1]);
        int y=solve(l)[2];
        return min(x,y);
    }
};
