class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> a, b;
        for(const auto& v : points) {
            a.insert(v[0] + v[1]);
            b.insert(v[0] - v[1]);
        }
        int res = 2e9;
        for(const auto& v : points) {
            a.erase(a.find(v[0] + v[1]));
            b.erase(b.find(v[0] - v[1]));
            res = min(res, max(*a.rbegin() - *a.begin(), *b.rbegin() - *b.begin()));
            a.insert(v[0] + v[1]);
            b.insert(v[0] - v[1]);
        }
        return res;
    }
};
