class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<pair<int, int>> sum(n), diff(n);

        for (int i = 0; i < n; ++i) {
            int x = points[i][0], y = points[i][1];
            sum[i] = {x + y, i};
            diff[i] = {x - y, i}; 
        }

        sort(sum.begin(), sum.end());
        sort(diff.begin(), diff.end());

        int ans = INT_MAX;

        int ds0 = sum[n - 1].first - sum[0].first;
        int ds1 = sum[n - 1].first - sum[1].first;
        int ds2 = sum[n - 2].first - sum[0].first;

        int dd0 = diff[n - 1].first - diff[0].first;
        int dd1 = diff[n - 1].first - diff[1].first;
        int dd2 = diff[n - 2].first - diff[0].first;

        int i = sum[0].second;

        if (diff[0].second == i) {
            int tmp = max(ds1, dd1);
            ans = min(ans, tmp);
        } 
        else if (diff[n - 1].second == i) {
            int tmp = max(ds1, dd2);
            ans = min(ans, tmp);
        }
        else {
            ans = min(ans, max(ds1, dd0));
        }

        i = sum[n - 1].second;
        if (diff[0].second == i) {
            ans = min(ans, max(ds2, dd1));
        }
        else if (diff[n - 1].second == i) {
            ans = min(ans, max(ds2, dd2));
        }
        else {
            ans = min(ans, max(ds2, dd0));
        }

        i = diff[0].second;
        if (sum[0].second == i) {
            ans = min(ans, max(ds1, dd1));
        }
        else if (sum[n - 1].second == i) {
            ans = min(ans, max(ds2, dd1));
        }
        else {
            ans = min(ans, max(ds0, dd1));
        }

        i = diff[n - 1].second;
        if (sum[0].second == i) {
            ans = min(ans, max(ds1, dd2));
        }
        else if (sum[n - 1].second == i) {
            ans = min(ans, max(ds2, dd2));
        }
        else {
            ans = min(ans, max(ds0, dd2));
        }

        return ans;
    }
};
