class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        points = [(x + y, x - y) for x, y in points]
        points.sort()
        option1 = points[:-1]
        option2 = points[1:]
        points.sort(key = lambda p: (p[1], p[0]))
        option3 = points[:-1]
        option4 = points[1:]
        def res(points):
            xes = [x for x, _ in points]
            yes = [y for _, y in points]
            return max(max(xes) - min(xes), max(yes) - min(yes))
        return min(res(option1), res(option2), res(option3), res(option4))
