/**
 * @param {number[][]} points
 * @return {number}
 */
var minimumDistance = function(points) {
    let minSum = points[0][0] + points[0][1];
    let minDiff = points[0][0] - points[0][1];
    let maxSum = points[0][0] + points[0][1];
    let maxDiff = points[0][0] - points[0][1];

    let minSumIndex = 0, maxSumIndex = 0, minDiffIndex = 0, maxDiffIndex = 0;

    cal(1, -1);

    let min, max;

    if((maxDiff - minDiff) > (maxSum - minSum)) {
        min = minDiffIndex;
        max = maxDiffIndex;
    }else {
        min = minSumIndex;
        max = maxSumIndex;
    }

    fill(min);
    cal(1, min);

    let res = Math.max(maxSum-minSum, maxDiff-minDiff);

    fill(max);
    cal(1, max);

    return Math.min(Math.max(maxSum-minSum, maxDiff-minDiff), res);

    function cal(start, x) {
        for(let i=start;i<points.length;i++) {
            if(i == x) continue;
            let sum = points[i][0] + points[i][1];
            let diff = points[i][0] - points[i][1];

            if(minSum > sum) {
                minSum = sum;
                minSumIndex = i;
            }else if(maxSum < sum) {
                maxSum = sum;
                maxSumIndex = i;
            }

            if(minDiff > diff) {
                minDiff = diff;
                minDiffIndex = i;
            }else if(maxDiff < diff) {
                maxDiff = diff;
                maxDiffIndex = i;
            }
        }
    }

    function fill(x) {
        if(x == 0) x = 1;
        else x = 0;
        minSum = points[x][0] + points[x][1];
        minDiff = points[x][0] - points[x][1];
        maxSum = points[x][0] + points[x][1];
        maxDiff = points[x][0] - points[x][1];
    }
};
