class Solution(object):
    def waysToReachStair(self, k):
            dp = defaultdict(int)
            def solve(k, i, jump, can):
                if i > k + 5:
                    return 0

                if jump > 31:
                    return 0

                if i in dp and dp[i][jump][can] != -1:
                    return dp[i][jump][can]

                ans = 1 if i == k else 0
                if can:
                    ans += solve(k, i - 1, jump, 0)
                if i + (1 << jump) <= k + 1:
                    ans += solve(k, i + (1 << jump), jump + 1, 1)

                if i not in dp:
                    dp[i] = [[-1] * 2 for _ in range(32)]

                dp[i][jump][can] = ans
                return ans

            return solve(k, 1, 0, 1)
