class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, j, down):
            if i > k+1:
                return 0

            if i == k:
                if down or k == 0:
                    return 1 + dfs(i + pow(2, j), j+1, False)
                else:
                    return 1 + dfs(i-1, j, True) + dfs(i + pow(2, j), j+1, False)

            if down:
                return dfs(i + pow(2, j), j+1, False)

            return dfs(i + pow(2, j), j+1, False) + dfs(i-1, j, True)

        return dfs(1, 0, False)
