class Solution:
    def waysToReachStair(self, k: int) -> int:
        self.k = k
        return self.dfs(1, 0, True)


    @lru_cache(None)
    def dfs(self, idx, jump, can_down):
        if idx - can_down > self.k + 1:
            return 0
        if idx < 0:
            return 0
        res = 0
        if idx == self.k:
            res = 1
        if can_down:
            res += self.dfs(idx - 1, jump, False)
        res += self.dfs(idx + pow(2, jump), jump + 1, True)
        # print(idx, jump, can_down, res)
        return res
