class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(n, jump, flag):
            if n>k+1:
                return 0
            ans = 0
            if n == k:
                ans+=1
            if not flag and n!=0:
                ans+=dfs(n-1, jump, 1)
            ans+=dfs(n+2**jump, jump+1, 0)
            return ans
        return dfs(1, 0, 0)
