class Solution {
public:
    int k;
    map<pair<int,pair<int,int>>,int> mp;
    int rec(long long s,int j,int ok){
        //cout << s << endl;
        if(s >= k+100) return 0;
        if(mp[{s,{j,ok}}]) return mp[{s,{j,ok}}]-1;
        int w1 = rec(s + (1ll<<j),j+1,0);
        int w2 = (ok ? 0 : rec(s-1,j,1));
        mp[{s,{j,ok}}] = (s==k) + w1 + w2 + 1;
        return (s==k) + w1 + w2;
    }
    int waysToReachStair(int k){
        this->k = k;
        return rec(1,0,0);
    }
};
