#define ll long long
class Solution {
public:
    ll count = 0;
    map<vector<ll>,ll>mp;
    ll solve(ll n,ll j,ll turn,ll k){
       if(n  > k + 1) return 0;
       if(mp.find({n,j,turn}) != mp.end()) return mp[{n,j,turn}];
       ll ans = 0;
       if(n == k) ans++;
       if(turn != 1 && n != 0){
           ans += solve(n + pow(2LL,j),j + 1,0LL,k) + solve(n - 1,j,1LL,k);
       }
       else ans += solve(n + pow(2LL,j),j + 1,0LL,k);
       return mp[{n,j,turn}] = ans;
    }
    int waysToReachStair(int k) {
        return  solve(1LL,0LL,-1LL,k*1LL);
    }
};
