class Solution {
public:
    int waysToReachStair(int k) {
        if(k==0)return 2;
        if(k==1)return 4;
        int ans =0;
        long long x = 0;
        long long diff = k - 1;
        for(long long i=0 ;i<35 ;i++){
            x += (1ll<<i);
            if(x >= diff){
                long long diff_req = x - diff;
                // cout<<x<<" "<<diff_req<<" "<<i+2<<endl;
                if(diff_req > i+2){
                    break;
                }
                long long z = 1;
                set<int> s;
                diff_req = min(1ll*diff_req , i+2 -diff_req);
                for(int j =1;j <= diff_req ; j++){
                    s.insert(j);
                }
                long long y = i+2;

                for(long long j = y - diff_req + 1 ; j<=y ;j++){
                    z*=(1ll*j);
                    for(auto it = (--s.end()) ; it != s.begin() ;it-- ){
                        if(z%(*it) == 0){
                            z/=(*it);
                            s.erase(it);
                        }
                    }
                }
                ans += z;
                // cout<<ans<<endl;
            }
        }
        return ans;
    }
};
