class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def go(stair,jump,good):
            ret = 1 if stair == k else 0
            if good and stair:
                ret += go(stair - 1, jump, False)

            if stair + (1 << jump) <= k + 1:
                ret += go(stair + (1 << jump), jump + 1, True)

            return ret
        ans = go(1,0,True)
        go.cache_clear()
        return ans
