class Solution:
    def __init__(self):
        self.memo = {} 

    def dp(self, k, i, jump, t):
        if (i, jump, t) in self.memo:
            return self.memo[(i, jump, t)]

        ans = i == k
        if t:
            ans += self.dp(k, i - 1, jump, 0)

        if i + (1 << jump) <= k + 1:
            ans += self.dp(k, i + (1 << jump), jump + 1, 1) 
        self.memo[(i, jump, t)] = ans
        return ans

    def waysToReachStair(self, k):
        return self.dp(k, 1, 0, 1)
