# step, jump, down, start

class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def rec(step, jump, down):
            res = 0
            if step > k + 1: return res
            if step == k:
                res += 1
            if down or step == 0:
                res += rec(step + 2 ** jump, jump + 1, False)
            else:
                res += rec(step + 2 ** jump, jump + 1, False) + rec(step - 1, jump, True)
            return res
        return rec(1, 0, False)
