from math import comb

def power_of_2(s):
    if s == 0:
        return -1

    j = 0
    while s&1 == 0:
        s >>= 1
        j += 1

    if s == 1:
        return j
    else:
        return -1

class Solution:
    def waysToReachStair(self, k: int) -> int:
        res = 0
        for p in range(50):
            cur = k + p
            j = power_of_2(cur)
            if j == -1:
                continue

            res += comb(j+1, p)
        return res
