class Solution {
public:
    unordered_map<int,int>dp[40][2];
    int solve(int i,int jump,bool last,int k)
    {
        if(i>k+5)
            return 0;
        if(dp[jump][last].count(i))
        return dp[jump][last][i];
        dp[jump][last][i]=(i==k);
        if(!last&&i)
            dp[jump][last][i]+=solve(i-1,jump,1,k);
        dp[jump][last][i]+=solve(i+(1<<jump),jump+1,0,k);
        return dp[jump][last][i];
    }
    int waysToReachStair(int k) {
        return solve(1,0,0,k);
    }
};
