#define ll long long
class Solution {
public:
    int C(ll n,ll r){
        long double sum = 1;
  // Calculate the value of n choose r using the binomial coefficient formula
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return (int)sum;
    }
    int pw(int n){
        if(n==0) return 1;
        int a = pw(n/2);
        a = a*a;
        if(n%2==1) a = a*2;
        return a;
    }
    int waysToReachStair(int k) {
        ll ret = 0;
        ll i=0;
        // vector<ll> fact;
        // fact.push_back(1);
        // ll t = 1;
        // while(t*fact[fact.size()-1] < 1e15){
        //     fact.push_back(t*fact[fact.size()-1]);
        //     t++;
        // }
        while(true){
            ll num = pw(i);
            ll st = num-k;
            if(st<0){
                i++;
                continue;
            }
            if(st>i+1) break;
            // cout<<i+1<<" "<<st<<endl;
            ret += C(i+1, st);
            // cout<<fact[15]<<endl;
            i++;
        }
        return ret;
    }
};
