class Solution {
    public int waysToReachStair(int k) {
        long ans = 0;
        for (int count = 1; count <= 31; count++) {
            int i = 1 << (count - 1);
            int using = i - k;
            if (using >= 0 && using <= count)
                ans += C(count, using);
        }

        return (int) ans;
    }

    long C(long n, long r) {
        long ans = 1;
        for (int i = 0; i < r; i++) {
            ans *= n - i;
            ans /= i + 1;
        }

        return ans;
    }
}
