class Solution:
    def waysToReachStair(self, k: int) -> int:

        memo = {}
        def solve(i, j, can):
            if (i, j, can) in memo:
                return memo[(i, j, can)]
            if i > k + 5:
                return 0
            if j > 31:
                return 0
            res = (i == k) 
            if can:
                res += solve(i - 1, j, 0)
            if (i + (1 << j) <= k + 1):
                res += solve(i + (1 << j), j + 1, 1)

            memo[(i, j, can)] = res
            return memo[(i, j, can)]


        return solve(1, 0, 1)
