class Solution {
public:

    int solve(int k, int stair, int jump, int used,  map<tuple<long long, long long, long long, int>, int > &dp ){

        if(k+2 < stair) return 0;
        if(dp.count( make_tuple(k, stair, jump, used))) return  dp[make_tuple(k, stair, jump, used)] ;
        int op1 = 0, op2 = 0;

        if(used && stair) op1 +=  solve(k, stair -1, jump, 0, dp);
        op2 +=   solve(k,  stair + pow(2, jump), jump+1, 1, dp);



        return dp[make_tuple(k, stair, jump, used)] = op1 + op2 + (stair == k);

    }

    int waysToReachStair(int k) {
        map<tuple<long long, long long, long long, int>, int > dp;
        return solve(k, 1, 0, 1, dp);
    }
};
