import math
class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4
        if k == 2:
            return 4
        if k == 4:
            return 2

        def non_adjacent_permutations(m, n):
            if m > n + 1:
                return 0
            return math.comb(n + 1, m)
        target = k - 1
        curr = 0
        jump = 0
        while curr < target:
            curr += 2**jump
            jump += 1
        subtract = curr - target
        return non_adjacent_permutations(subtract, jump)
