#define ll long long
ll one = 1;
class Solution {
public:
    map<ll, map<ll, ll>> dp[2];
    ll calc(ll k, ll i, ll jump, bool pv) {
        if(k < i - 1) return 0;
        if(dp[pv][i].count(jump)) return dp[pv][i][jump];
        // cout<<pv<<' '<<i<<' '<<jump<<endl;
        ll ans = (i == k) + calc(k, i + (one << jump), jump + 1, 0);
        if(!pv) ans += calc(k, i - 1, jump, 1);
        return dp[pv][i][jump] = ans;
    }
    int waysToReachStair(int k) {
        dp[0].clear(), dp[1].clear();
        return calc(k, 1, 0, 0);
    }
};
