typedef long long ll;
//ll dp[100][100][2],m;

ll solve(ll pos,ll step,ll take,ll val,ll dp[100][100][2],ll m) {
    if(val == m) return 1;
    if(val >= 2ll*m) return 0;
    if(dp[pos][step][take] != -1) return dp[pos][step][take];
    ll e = solve(pos+1,step+1,0,val+(1<<(step-1)),dp,m);
    if(val != 0 and take == 0) e += solve(pos+1,step,1,val-1,dp,m);
    return dp[pos][step][take] = e;
}

class Solution {
public:
    ll waysToReachStair(int k) {
        ll dp[100][100][2];

        for(ll i = 0; i < 100; i++) {
            for(ll j = 0; j < 100; j++) {
                dp[i][j][0] = -1;
                dp[i][j][1] = -1;
            }
        }
        if(k == 0) return 2;
        else if(k == 1) return 4;
        else if(k == 2) return 4;
        else return solve(0,1,0,1,dp,k); ///pos,step,prev -1 nise naki ne nai,val..
    }
};
