/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {
  const memo = {};
  const dp = (i, jump, prev) => {
    if (i == 1 && jump == 0 && prev == 0) {
      return 1;
    }
    if (i < 0 || jump < 0) {
      return 0;
    }
    const idx = `${i},${jump},${prev}`;
    if (memo[idx] !== undefined) {
      return memo[idx];
    }
    let res = 0;
    if (prev) {
      res = dp(i + 1, jump, 0);
    } else {
      res = dp(i - 2 ** (jump-1), jump-1, 0) + dp(i - 2 ** (jump-1), jump-1, 1);
    }
    memo[idx] = res;
    return res;
  }

  let res = 0;
  for (let j = Math.ceil(Math.log2(k+1)) + 2; j >= 0; --j) {
    let r = dp (k, j, 0) + dp(k, j, 1);
    res += r;
  }
  // console.log({k, memo})
  return res;
};
