class Solution {
public:
    int waysToReachStair(int k) {

        if(k == 0) return 2;
        int ans = (k == 1);
        for(int i = 0; i < 33; i++)
        {
            int cur = check(k+i-1);
            if(cur)
            {
                ans += ways(i,cur);
            }
        }

        return ans;


    }

    int ways(int i, int ones)
    {
        if(i == 0) return 1;
        //cout << i << ", " << ones << endl;
        if(i > ones+1) return 0;
        vector<vector<vector<long long>>> dp(i+ones+1,vector<vector<long long>>(i+1,vector<long long>(2)));
        dp[1][0][0] = 1;
        dp[1][1][1] = 1;
        for(int j = 2; j < i+ones+1; j++)
        {
            for(int k = 0; k < i+1; k++)
            {
                dp[j][k][0] = dp[j-1][k][1]+dp[j-1][k][0];
                if(k) dp[j][k][1] = dp[j-1][k-1][0];
                //cout << j << ", " << k << "; " << dp[j][k][0] << ", " << dp[j][k][1] << " : ";
            }
            //cout << endl;
        }

        return dp[i+ones][i][0]+dp[i+ones][i][1];
    }

    int check(int x)
    {
        for(int i = 1; i < 31; i++)
        {
            if((1<<i)-1 == x) return i;
        }

        return 0;
    }
};
