class Solution {
public:
    map<pair<int,int>,int> mp;
    int solve(long long step, int j, bool can, int k){
        //cout << step << ' ' << j << '\n';
        if(step==k+1 && !can || step > k+1) return 0;
        int i = j;
        if(can) i=-i;
        if(mp.find({step,i})!=mp.end()) return mp[{step,i}];
        int x = 0;
        if(can && step) x=solve(step-1,j,0,k);
        int y = solve(step+pow(2,j),j+1,1,k);
        return mp[{step,i}]=(step==k)+x+y;
    }
    int waysToReachStair(int k) {
        return solve(1,0,1,k);
    }
};
