class Solution {
    private:
    unordered_map<int, vector<vector<int>>> dp;

    int solve( int i, int j, bool c,int k) {
        if (j >= 32)
            return 0;
        if (i > k + 10)
            return 0;

        if (dp.count(i) && dp[i][j][c] != -1)
            return dp[i][j][c];

        int a = 0;
        if(i==k) a=1;

        if (i + (1 << j) <= k + 1)
            a += solve(i + (1 << j), j + 1, true,k);
        if (c)
            a += solve( i - 1, j, false,k);

        if (!dp.count(i)) {
            dp[i].resize(32, vector<int>(2, -1));
        }

        return dp[i][j][c] = a;
    }

public:
    int waysToReachStair(int k) {
        bool jump=true;
        return solve(1, 0, jump,k);
    }
};
