class Solution {
public:


    int sol(int i, int flag, int j, int k,map<vector<int>,int>&mp ){

        if(i>k+2){
            return 0;
        }
        if(j>30) return 0;
        if(mp.find({i,flag,j})!=mp.end()) return mp[{i,flag,j}];
        int K=1ll*(1<<j);
        int ways=0;
        if(i==k) ways=1;
        if(i+K<=INT_MAX) ways+=sol(i+K,1,j+1,k,mp);
        if(flag==1){
            ways+=sol(i-1,0,j,k,mp);
        }
       // cout<<i<<' '<<flag<<' '<<j<<' '<<ways<<endl;
        return mp[{i,flag,j}]=ways;
    }



    int waysToReachStair(int k) {
        map<vector<int>,int>mp;
        return sol(1,1,0,k,mp);

    }
};
