class Solution:
    def waysToReachStair(self, k: int) -> int:
        @functools.cache
        def dfs(i, jump, method) -> int:
            if i > k + 1:
                return 0

            r = 0
            if i == k:
                r += 1
            r += dfs(i + 2 ** jump, jump + 1, 1)
            if method != 0 and i > 0:
                r += dfs(i - 1, jump, 0)
            return r

        return dfs(1, 0, None)
