    long long nCr(long long n, long long r) {
        if (r > n) return 0;
        if (r == 0 || n == r) return 1;
        double res = 0;
        for (int i = 0; i < r; i++) {
            res += log(n-i) - log(i+1);
        }
        return (long long)round(exp(res));
    }
public:
    int waysToReachStair(int k) {
        if (k == 0) return 2;
        long long answer = 0;
        long double n = ceil(log2(k));
        long long m = pow(2, n) - k;
        while(m <= n + 1) {
            answer += nCr(n + 1, m);
            n += 1;
            m = pow(2, n) - k;
        }
        return answer;
    }
